import React, { useState } from "react";

const Form = () => {
    const [fullName, setFullName] = useState({
        fname: '',
        lname: '',
        email: '',
        phone: '',
        password: ''
    });

    const inputEventt = (event) => {
        console.log(event.target.value);
        // setFullName(event.target.name);

        // const value = event.target.value;
        // const name = event.target.name;
        const { value, name } = event.target;//forarray destructuring;
        setFullName((preValue) => {
            if (name === 'fname') {
                return {
                    fname: value,
                    lname: preValue.lname,
                    email: preValue.email,
                    phone: preValue.phone,
                    password: preValue.password
                };

            } else if (name === 'lname') {
                return {
                    fname: preValue.fname,
                    lname: value,
                    email: preValue.email,
                    phone: preValue.phone,
                    password: preValue.password
                };

            } else if (name === 'email') {
                return {
                    fname: preValue.fname,
                    lname: preValue.lname,
                    email: value,
                    phone: preValue.phone,
                    password: preValue.password
                };

            } else if (name === 'phone') {
                return {
                    fname: preValue.fname,
                    lname: preValue.lname,
                    email: preValue.email,
                    phone: value,
                    password: preValue.password
                };
            } else if (name === 'password') {
                return {
                    fname: preValue.fname,
                    lname: preValue.lname,
                    email: preValue.email,
                    phone: preValue.phone,
                    password: value

                }
            }
        }

        )
    }


    const onSubmits = (event) => {
        event.preventDefault();// for form default me refresh na ho
        alert("form submitted");

    }

    return (<>
        <div>
            <form onSubmit={onSubmits}>
                <div>

                    <h1> Hello, {fullName.fname}  {fullName.lname}</h1>
                    <div className="firstcontainer">
                        <label htmlFor="vessel" >Vessel(s)<span>*</span></label>
                        <input type="text" placeholder="Enter your name" onChange={inputEventt} value={fullName.fname} name='fname' />
                        <label htmlFor="quantity" >Quantity<span>*</span></label>
                        <input type="text" placeholder="Enter your last name" onChange={inputEventt} value={fullName.lname} name='lname' />
                        <label htmlFor="offer" >Offer Price...<span>*</span></label>
                    </div>
                    <input type="email" placeholder="Enter your email" onChange={inputEventt} value={fullName.email} name='email' />
                    <input type="phone" placeholder="Enter your phone number" onChange={inputEventt} value={fullName.phone} name='phone' />
                    <input type="number" placeholder="Enter your password" onChange={inputEventt} value={fullName.password} name='password' />
                    {/* <button onClick={onSubmits}> Submit </button>// */}
                    <button type="submit"> Submit Me </button>
                </div>
            </form>
        </div>

    </>
    );
}

export default Form;
